update cyy_room set id = #{id} 

	,buildingId = #{buildingId}


	,roomName = #{roomName}


	,currentGuest = #{currentGuest}


	,roomPrice = #{roomPrice}


	,lastRent = #{lastRent}


	,lastRentTime = #{lastRentTime}


	,nextRentTime = #{nextRentTime}


	,desc = #{desc}


	,createTime = #{createTime}

,updateTime = now() where roomName = #{roomName} and buildingId = #{buildingId}

insert into cyy_room(
buildingId, roomName, currentGuestId, roomPrice, lastRent, lastRentTime, nextRentTime, desc, createTime, updateTime
) values(
#{buildingId}, #{roomName}, #{currentGuestId}, #{roomPrice}, #{lastRent}, #{lastRentTime}, #{nextRentTime}, #{desc}, #{createTime}, now()
)
